const canvas = document.querySelector('#hero-img');
const scene = new THREE.Scene();
const loader = new THREE.GLTFLoader();

const ships = [];
const parallaxSpeeds = [0.3, 0.5, 0.7];

parallaxSpeeds.forEach((speed, i) => {
  loader.load(`ship-${i}.gltf`, (gltf) => {
    const ship = gltf.scene;
    scene.add(ship);
    ships.push({ model: ship, speed });
  });
});

window.addEventListener('scroll', () => {
  const scrollY = window.scrollY;
  ships.forEach(ship => {
    ship.model.position.x = -scrollY * ship.speed * 0.05;
    ship.model.position.y = scrollY * ship.speed * 0.08;
  });
});

const scenes = {
  hero: document.querySelector('#hero-scene'),
  port: document.querySelector('#port-scene')
};

gsap.timeline({
  scrollTrigger: {
    trigger: '#hero',
    start: 'top top',
    end: 'bottom top',
    scrub: 1.5
  }
})
.to(scenes.hero, { opacity: 0.3, y: -100 })
.to(scenes.port, { opacity: 1, y: 0 }, '<');

gsap.utils.toArray('.port-element').forEach((el, i) => {
  const speed = el.dataset.parallaxSpeed || 0.5;
  gsap.to(el, {
    y: () => window.scrollY * speed,
    ease: 'none',
    scrollTrigger: {
      trigger: '#port-scene',
      start: 'top bottom',
      end: 'bottom top',
      scrub: true
    }
  });
});

gsap.from('.description-text-overlay', {
  opacity: 0,
  y: 50,
  duration: 0.8,
  scrollTrigger: {
    trigger: '.port-scene',
    start: 'top 60%',
    toggleActions: 'play none none reverse'
  }
});

const tl = gsap.timeline({
  scrollTrigger: {
    trigger: '.mission-section',
    start: 'top 70%'
  }
});
tl.from('.mission-heading', { opacity: 0, scale: 0.8, y: 50, duration: 0.8 })
  .from('.mission-subheading', { opacity: 0, y: 30, duration: 0.6 }, '-=0.4');

gsap.from('.brand-logo', {
  opacity: 0,
  y: 30,
  duration: 0.6,
  stagger: 0.15,
  scrollTrigger: {
    trigger: '.brands-section',
    start: 'top 60%'
  }
});

// Split text into words/characters for animation
const text = new SplitType('.value-prop-heading', { types: 'words,chars' });
gsap.from(text.chars, {
  opacity: 0,
  y: 20,
  duration: 0.05,
  stagger: 0.02,
  scrollTrigger: {
    trigger: '.value-prop-section',
    start: 'top 60%'
  }
});

gsap.from('.connection-line', {
  strokeDashoffset: 1000,
  duration: 2,
  stagger: 0.1,
  ease: 'none',
  scrollTrigger: { trigger: '.diagram-section', start: 'top center' }
});

gsap.from('.diagram-node', {
  opacity: 0,
  scale: 0.8,
  duration: 0.6,
  stagger: 0.1,
  ease: 'back.out(1.7)'
});

// Hover interactions
document.querySelectorAll('.diagram-node').forEach(node => {
  node.addEventListener('mouseenter', () => {
    gsap.to(node, { scale: 1.1, duration: 0.3 });
    // Show tooltip
  });
});

gsap.from('.value-description p', {
  opacity: 0,
  y: 30,
  duration: 0.8,
  stagger: 0.3,
  scrollTrigger: {
    trigger: '.value-description',
    start: 'top 70%'
  }
});
gsap.from('.cta-button', { opacity: 0, duration: 0.5, delay: 1 });

// Horizontal scroll snap
section {
  scroll-snap-type: x mandatory;
  overflow-x: scroll;
  display: flex;
}
.card {
  scroll-snap-align: start;
  min-width: 400px;
}

// Or GSAP horizontal scroll
gsap.to('.feature-cards', {
  x: () => -(cards.scrollWidth - viewport.width),
  ease: 'none',
  scrollTrigger: {
    trigger: '.feature-showcase',
    start: 'top top',
    end: 'bottom bottom',
    scrub: 1,
    pin: true
  }
});

// 3D tilt on illustrations
gsap.to('.feature-illustration', {
  rotateY: 10,
  scrollTrigger: {
    trigger: '.feature-card',
    scrub: 0.5
  }
});

const tl = gsap.timeline({
  scrollTrigger: {
    trigger: '.data-ingestion-section',
    start: 'top 60%',
    toggleActions: 'play none none reverse'
  }
});

tl.fromTo('.email-card', 
  { opacity: 0, y: -30 },
  { opacity: 1, y: 0, duration: 0.6, ease: 'power2.out' }
)
.fromTo('.connection-line', 
  { strokeDashoffset: 1000 },
  { strokeDashoffset: 0, duration: 1, ease: 'none' },
  '-=0.3'
)
.to('.highlight-container-number', {
  backgroundColor: 'rgba(78, 115, 214, 0.2)',
  duration: 0.4
}, '-=0.4')
.to('.highlight-date', {
  backgroundColor: 'rgba(78, 115, 214, 0.2)',
  duration: 0.4
}, '-=0.2');

const tl = gsap.timeline({
  scrollTrigger: {
    trigger: '.data-ingestion-section',
    start: 'top center'
  }
});

tl.from('.bill-of-lading-card', {
  opacity: 0,
  scale: 0.95,
  duration: 0.8,
  ease: 'power2.out'
})
.from('.connection-line', {
  strokeDashoffset: 500,
  duration: 0.6,
  stagger: 0.15,
  ease: 'power1.inOut'
}, '-=0.4')
.from('.nauta-logo-glow', {
  opacity: 0,
  filter: 'blur(0px)',
  duration: 0.5
}, '-=0.6');

// Animated ship route
gsap.to('.ship-marker', {
  motionPath: {
    path: '.route-path',
    align: '.route-path',
    autoRotate: true
  },
  duration: 10,
  repeat: -1,
  ease: 'none'
});

// Progress bar
gsap.to('.progress-fill', {
  width: '70%',
  duration: 1.5,
  ease: 'power2.out',
  scrollTrigger: {
    trigger: '.product-control-section',
    start: 'top center'
  }
});

// Status badge
gsap.from('.status-badge', {
  opacity: 0,
  scale: 0.9,
  duration: 0.5,
  delay: 0.8
});

// Play button pulse
gsap.to('.play-button', {
  scale: 1.1,
  duration: 1,
  repeat: -1,
  yoyo: true,
  ease: 'sine.inOut'
});

// Counter animation
gsap.from('.stat-number', {
  textContent: 0,
  duration: 2,
  ease: 'power1.out',
  snap: { textContent: 1 },
  stagger: 0.2,
  scrollTrigger: {
    trigger: '.stats-section',
    start: 'top 70%'
  }
});

// Icon entrance
gsap.from('.stat-icon', {
  opacity: 0,
  scale: 0.8,
  duration: 0.6,
  stagger: 0.15,
  ease: 'back.out(1.7)'
});

gsap.from('.testimonial-card', {
  opacity: 0,
  y: 50,
  duration: 0.8,
  stagger: 0.3,
  scrollTrigger: {
    trigger: '.testimonials-section',
    start: 'top 60%'
  }
});

gsap.from('.author-card', {
  opacity: 0,
  duration: 0.6,
  delay: 0.5
});

